using UnityEngine;using UnityEngine.UI;public class ToIntIndicators : MonoBehaviour{    [SerializeField]    TextAndSlider original;    [SerializeField]    TextAndSlider round;    [SerializeField]    TextAndSlider ceil;    [SerializeField]    TextAndSlider floor;    void Awake()    {        original.AddListener(value =>        {            round.value = Mathf.Round(value);            ceil.value = Mathf.Ceil(value);            floor.value = Mathf.Floor(value);        });    }}